const body=document.body,menu=document.querySelector(".header__menu");function toggleElement(e,t){t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("lock")):(t.classList.add("active"),e.classList.add("lock"))}function burgerHandler(){requestAnimationFrame(()=>toggleElement(body,menu))}const popup=document.querySelector(".popup"),requestButtons=document.querySelectorAll(".request__btn"),closePopup=document.querySelector(".popup__close");function popupHandler(){requestAnimationFrame(()=>toggleElement(body,popup))}function hidePopup(){popup.classList.remove("active"),body.classList.remove("lock")}function toggleHeader(e){let o=window.scrollY||document.documentElement.scrollTop,n,c=0,d=0;const s=document.querySelector(".header");window.addEventListener("scroll",function(){if(n=window.scrollY||document.documentElement.scrollTop,n>o?c=2:n<o&&(c=1),c!==d){var e=c,t=n;if(e===2&&t>100){s.classList.add("hide");d=e}else if(e===1){s.classList.remove("hide");d=e}}o=n})}function addFixedClassToHeader(){const e=document.querySelector(".header");let t=e.offsetHeight+300;console.log(t),window.addEventListener("scroll",function(){this.scrollY>t?e.classList.add("fixed"):e.classList.remove("fixed")})}document.addEventListener("DOMContentLoaded",function(e){const t=document.querySelector(".header__burger"),o=(window.innerWidth<=1599&&t.addEventListener("click",burgerHandler),window.matchMedia("(max-width: 1599px)"));o.addEventListener("change",function(e){e.matches?t.addEventListener("click",burgerHandler):(t.removeEventListener("click",burgerHandler),menu.classList.remove("active"),body.classList.remove("lock"))}),requestButtons.forEach(e=>e.addEventListener("click",popupHandler)),closePopup.addEventListener("click",hidePopup),toggleHeader(),addFixedClassToHeader()});