const body=document.body,menu=document.querySelector(".header__menu");function burgerToggle(e,n){n.classList.contains("active")?(n.classList.remove("active"),e.classList.remove("lock")):(n.classList.add("active"),e.classList.add("lock"))}function burgerHandler(){requestAnimationFrame(()=>burgerToggle(body,menu))}document.addEventListener("DOMContentLoaded",function(e){const n=document.querySelector(".header__burger"),t=(window.innerWidth<=1599&&n.addEventListener("click",burgerHandler),window.matchMedia("(max-width: 1599px)"));t.addEventListener("change",function(e){e.matches?n.addEventListener("click",burgerHandler):(n.removeEventListener("click",burgerHandler),menu.classList.remove("active"),body.classList.remove("lock"))})});